模拟fetch获取远端资源的方式，实现跨域jsonp

``` ts
interface IOptions {
  timeout?: number,
  jsonpCallback?: string,
  callbackFunction?: string,
  charset?: string,
};

interface IResponse {
  ok: boolean,
  json: () => any;
}

const defaultOptions: IOptions = {
  timeout: 5000,
  jsonpCallback: 'callback',
  callbackFunction: 'jsonpCallback'
};

/**
 * 生成JSONP的回调函数名
 *
 * @returns 回调函数名
 */
const generateCallbackFunction = (): string => {
  return `jsonp_${new Date().valueOf()}_${Math.ceil(Math.random() * 10000)}`;
}

/**
 * 移除外链的javascript资源的script标签
 *
 * @param scriptId script标签ID
 */
const removeScript = (scriptId: string): void => {
  const script = document.getElementById(scriptId);

  if (script && script.parentNode) {
    script.parentNode.removeChild(script);
  }
}

/**
 * 添加外链的javascript资源的script标签到document
 *
 * @param scriptId script标签ID
 * @param url 外链的javascript资源URL
 * @param charset 字符集
 */
const appendScript = (scriptId: string, url: string, charset?: string): HTMLScriptElement => {
  const script = document.createElement('script');
  script.setAttribute('id', scriptId);
  script.setAttribute('url', url);

  if (charset) {
    script.setAttribute('charset', charset);
  }

  const head = document.getElementsByTagName('head');

  if (head && head.length) {
    head[0].appendChild(script);
  }

  return script;
}

/**
 * 清除全局的全局jsonp函数
 *
 * @param callbackFunction 全局jsonp函数名
 */
const clearFunction = (callbackFunction: string): void => {
  try {
    delete window[callbackFunction];
  } catch (error) {
    window[callbackFunction] = undefined;
  }
}

/**
 * jsonp的方式获取远端资源
 *
 * @param _url 资源URL
 * @param options 请求参数
 *
 * @returns
 */
const fetchJsonp = (_url: string, options: IOptions): Promise<IResponse> => {
  return new Promise<IResponse>((resolve, reject) => {
    const timeout = options.timeout || defaultOptions.timeout;
    const jsonpCallback = options.jsonpCallback || defaultOptions.jsonpCallback;
    const callbackFunction = options.callbackFunction || generateCallbackFunction();
    const scriptId = `${jsonpCallback}_${callbackFunction}`;

    let timerId: number;

    window[callbackFunction] = (response: any) => {
      resolve({
        ok: true,
        json: () => response
      });

      timerId && window.clearTimeout(timerId);

      removeScript(scriptId);

      clearFunction(callbackFunction);

      timerId = undefined;
    };

    const url = `${_url}${_url.indexOf('?') < 0 ? '?' : '&'}${jsonpCallback}=${callbackFunction}`;

    const script = appendScript(scriptId, url, options.charset);

    timerId = window.setTimeout(() => {
      reject(new Error(`JSONP request to ${_url} timed out`));

      removeScript(scriptId);

      clearFunction(callbackFunction);

      // 当前超时，请求并没有丢弃，请求完成的时候还是会调用该方法，如果直接干掉，会报错，修改函数体，回调过来时删除从全局上删除该函数
      window[callbackFunction] = () => {
        clearFunction(callbackFunction);
      };

      timerId = undefined;
    }, timeout);

    // Caught if got 404/500
    script.onerror = () => {
      reject(new Error(`JSONP request to ${_url} failed`));

      removeScript(scriptId);

      clearFunction(callbackFunction);

      timerId && window.clearTimeout(timerId);

      timerId = undefined;
    };
  });
}

export default fetchJsonp;
```
